import { useEffect, useState } from "react";
import moment from "moment-timezone";

function App() {
  const [timeZone, setTmeZone] = useState("India Standard Time");
  const [time, setTime] = useState(moment().tz(timeZone).format("LTS"));

  useEffect(() => {
    setInterval(() => setTime(moment().format("LTS")), 1000);
  }, [time]);

  return <h1>{time}</h1>;
}

export default App;
