import { useEffect, useState } from "react";
import moment from "moment-timezone";

function App() {
  const [timeZone, setTimeZone] = useState("India Standard Time");
  const [time, setTime] = useState(moment().tz(timeZone).format("LTS"));

  useEffect(() => {
    setInterval(() => setTime(moment().format("LTS")), 1000);
  }, [time]);

  return (
    <>
      <h1>{time}</h1>;
      <button onClick={() => setTimeZone("America/Los_Angeles")}>
        America
      </button>
    </>
  );
}

export default App;
