import React, { useState } from "react";
import { Button } from "react-bootstrap";

export default function ControlButtons(props) {
  const [timerValue, stTimerValue] = useState();
  const style = {
    fontSize: "20px",
    padding: "10px 30px 10px 30px",
    fontWeight: "bolder",
  };
  const StartButton = (
    <Button variant="outline-success" style={style} onClick={props.handleStart}>
      Start
    </Button>
  );
  const ActiveButtons = (
    <>
      <Button
        className="mx-2"
        style={style}
        variant="outline-danger"
        onClick={props.handleReset}
      >
        Reset
      </Button>
      <Button
        variant="outline-warning"
        style={style}
        onClick={props.handlePauseResume}
      >
        {props.isPaused ? "Resume" : "Pause"}
      </Button>
    </>
  );

  return (
    <div className="Control-Buttons">
      <div style={{ marginBottom: "10px" }}>
        {props.active ? ActiveButtons : StartButton}
      </div>
      <input name="time" value={timerValue} />
      <Button variant="outline-info" onClick={props.handleTimer}>
        Add Timer
      </Button>
    </div>
  );
}
