import React from "react";
import { Button, ButtonGroup } from "react-bootstrap";

export default function ControlButtons(props) {
  const StartButton = (
    <Button
      variant="outline-success"
      style={{ fontSize: "25px", padding: "10px 20px 10px 20px" }}
      onClick={props.handleStart}
    >
      Start
    </Button>
  );
  const ActiveButtons = (
    <ButtonGroup>
      <Button className="mx-2" variant="danger" onClick={props.handleReset}>
        Reset
      </Button>
      <Button variant="warning" onClick={props.handlePauseResume}>
        {props.isPaused ? "Resume" : "Pause"}
      </Button>
    </ButtonGroup>
  );

  return (
    <div className="Control-Buttons">
      <div>{props.active ? ActiveButtons : StartButton}</div>
    </div>
  );
}
