import React, { useEffect, useState } from "react";
import Timer from "./Timer";
import ControlButtons from "./ControlButtons";

function StopWatch() {
  const [isActive, setIsActive] = useState(false);
  const [isPaused, setIsPaused] = useState(true);
  const [time, setTime] = useState(0);
  const [revserse, setReverse] = useState(10);
  const [timerValue, setTimerValue] = useState(0);

  useEffect(() => {
    document.title = "Stop-Watch";
    let interval = null;
    if (time > 0) {
      handleReset();
    }

    if (isActive && isPaused === false) {
      interval = setInterval(() => {
        setTime((time) => time + revserse);
      }, 10);
    } else {
      clearInterval(interval);
    }
    return () => {
      clearInterval(interval);
    };
  }, [isActive, isPaused, revserse, time]);

  const handleStart = () => {
    setIsActive(true);
    setIsPaused(false);
  };

  const handlePauseResume = () => {
    setIsPaused(!isPaused);
  };

  const handleReset = () => {
    setIsActive(false);
    setTime(0);
    setReverse(10);
  };
  const handleTimer = () => {
    if (!(timerValue >= 0)) {
      alert("Enter Valid Time In Minutes");
      setTimerValue(0);
    } else {
      setReverse(-10);
      setTime(timerValue * 60000);
    }
  };
  const handleTimerChange = (e) => {
    setTimerValue(e.target.value);
  };

  return (
    <div className="stop-watch">
      <div className="timeZone">Stop-Watch</div>

      <Timer time={time} />

      <ControlButtons
        active={isActive}
        isPaused={isPaused}
        handleStart={handleStart}
        handlePauseResume={handlePauseResume}
        handleReset={handleReset}
        handleTimer={handleTimer}
        timerValue={timerValue}
        setTimerValue={setTimerValue}
        handleTimerChange={handleTimerChange}
      />
    </div>
  );
}

export default StopWatch;
