import React, { useEffect, useState } from "react";
import moment from "moment-timezone";

function App() {
  const [timeZone, setTimeZone] = useState("India Standard Time");
  const [time, setTime] = useState(moment().tz(timeZone).format("LTS"));

  useEffect(() => {
    const interval = setInterval(() => {
      setTime(moment().tz(timeZone).format("LTS"));
    }, 1000);
    debugger;
    return () => clearInterval(interval);
  }, [timeZone]);

  const handleTimeZoneChange = (newTimeZone) => {
    setTimeZone(newTimeZone);
  };

  return (
    <>
      <h1>{time}</h1>
      <h2>{timeZone}</h2>
      <button onClick={() => handleTimeZoneChange("America/Los_Angeles")}>
        America
      </button>
    </>
  );
}

export default App;
